import {useState} from "react";
import {Button} from "react-bootstrap";

function Job({job}) {
    const [cur, setCur] = useState('무관');

    return (
        <div>
            <Button className={(cur === '무관' ? "activeButton" : "inactiveButton")}>무관</Button>
            {job.map(j =>
                <Button className={(cur === j ? "activeButton" : "inactiveButton")}>{j}</Button>
            )}
        </div>
    )
}

export default Job;
