import {useState} from "react";
import Main from "../../component/js/Main";
import Post from "../../component/js/Post";
import "../css/body.css";

function Body({setFilter, postListApi}) {
    const [page, setPage] = useState(<Main setFilter={setFilter} postListApi={postListApi}/>);
    const [pageName, setPageName] = useState('main');

    function changePage() {
        setPage((pageName === 'main' ? <Post/> : <Main setFilter={setFilter}  postListApi={postListApi}/>));
        setPageName((pageName === 'main' ? 'post' : 'main'));
    }

    return (
        <div className="body">
            {page}
        </div>
    )
}

export default Body;
