import '../css/post-list.css';
import {useEffect, useRef, useState} from "react";
import axios from "axios";
import Post from "./Post";
import { useInView } from 'react-intersection-observer';

function PostList({postListApi}) {
    const [posts, setPosts] = useState([]);
    const postNum = useRef(0);
    const nextExist = useRef(true);
    const [ref, inView] = useInView({threshold:0.2});

    useEffect(() => {
        if ((inView && nextExist.current) || postNum.current === 0) {
            console.log("iin");
            axios.get(postListApi + "offset=" + postNum.current)
                .then((response) => {
                    const arrPost = [];
                    const curDate = new Date();
                    const len = response.data.length;

                    response.data.forEach(p => {
                        if (curDate < new Date(p.closingDate))
                            arrPost.push(p)
                    });
                    if (postNum.current !== 0) {
                        setPosts(prevPosts => prevPosts.concat(arrPost));
                        postNum.current += len;
                    } else {
                        setPosts(arrPost);
                        postNum.current = len;
                    }
                    if (len !== 20) {
                        nextExist.current = false;
                    }
            });
        }
    }, [inView, postListApi]);

    return (
        <div className="postList">
            {posts.map(post => <Post key={post.postId} post={post}/>)}
            <div ref={ref} id="postListEndPoint">test</div>
        </div>
    )
}

export default PostList;
