import "./district/ProvinceData";
import "./district/DistrictData";
import "./district/AreaData";
import {Button} from "react-bootstrap";
import {useState} from "react";

import PROVINCE_LIST from "./district/ProvinceData";
import DISTRICT_LIST from "./district/DistrictData";
import AREA_LIST from "./district/AreaData";

function District() {
    const [curProvince, setCurProvince] = useState('무관');
    const [curDistrict, setCurDistrict] = useState('무관');
    const [curArea, setCurArea] = useState('무관');

    const [districtList, setDistrictList] = useState([]);
    const [areaList, setAreaList] = useState([]);

    function fixProvince(target) {
        setCurProvince(target);
        setCurDistrict('무관');
        setCurArea('무관');
        {setDistrictList(
            DISTRICT_LIST.at(
                DISTRICT_LIST.findIndex((e) => e.province === target)
            ).districts
        );}
    }

    function fixDistrict(target) {
        setCurDistrict(target);
        setCurArea('무관');
        {setAreaList(
            AREA_LIST.at(
                AREA_LIST.findIndex((e) => e.district === target)
            ).area
        );}
    }

    function fixArea(target) {
        setCurArea(target);
    }

    return (
        <div>
            <p>지역</p>
            <div>
                <Button
                    key="지원종류무관"
                    className={(curProvince === '무관' ? "activeButton" : "inactiveButton")}
                    onClick={() => fixProvince('무관')}
                >
                    무관
                </Button>
                {PROVINCE_LIST.map(province =>
                    <Button
                        key={province}
                        className={(curProvince === province ? "activeButton" : "inactiveButton")}
                        onClick={() => fixProvince(province)}
                    >
                        {province}
                    </Button>
                )}
            </div>
            <div>
                {districtList.map(district =>
                    <Button
                        key={district}
                        className={(curDistrict === district ? "activeButton" : "inactiveButton")}
                        onClick={() => fixDistrict(district)}
                    >
                        {district}
                    </Button>
                )}
            </div>
            <div>
                {areaList.map(area =>
                    <Button
                        key={area}
                        className={(curArea === area ? "activeButton" : "inactiveButton")}
                        onClick={() => fixArea(area)}
                    >
                        {area}
                    </Button>
                )}
            </div>
        </div>
    )
}

export default District;
