import './App.css';
import Header from './layout/js/Header';
import {useRef, useState} from "react";
import Main from "./component/js/Main";
import PostPage from "./component/js/PostPage";

function App() {
    const filter = useRef(['', '', '', '', '', '']);
    const changePostListApi = useRef("");

    const getFilter = () => filter.current;
    const setFilter = (index, condition) => {filter.current[index] = condition};

    const [page, setPage] = useState(<Main setFilter={setFilter} ref={changePostListApi}/>);
    const [pageName, setPageName] = useState('main');


    function changePage() {
        setPage((pageName === 'main' ? <PostPage/> : <Main setFilter={setFilter} ref={changePostListApi}/>));
        setPageName((pageName === 'main' ? 'post' : 'main'));
    }

    return (
        <div className="App">
            <Header getFilter={getFilter}/>
            {page}
        </div>
    );
}

export default App;
