import './App.css';
import Header from './layout/js/Header';
import Body from './layout/js/Body'
import {useRef} from "react";
import {Button} from "react-bootstrap";

function App() {
    const filter = useRef(['', '', '']);

    const getFilter = () => filter.current;
    const setFilter = (index, condition) => {filter.current[index] = condition};

    return (
        <div className="App">
            <Header getFilter={getFilter}/>
            <Button onClick={() => setFilter(1, 'test')}>test</Button>
            <Body setFilter={setFilter}/>
        </div>
    );
}

export default App;
