import '../css/post-list.css';
import {forwardRef, useEffect, useState} from "react";
import axios from "axios";
import Post from "./Post";

function PostList({ref}) {
    const [posts, setPosts] = useState([]);
    const [postListApi, setPostListApi] = useState("http://localhost:3000/postData.json");

    const setCurPostListApi = forwardRef((conditions) =>
        setPostListApi("http://localhost:3000/postData.json" + conditions));

    useEffect(() => {
        axios.get(postListApi)
            .then((response) => {
                const arrPost = [];
                const curDate = new Date();
                response.data.forEach(p => {
                    if (curDate < new Date(p.closingDate))
                        arrPost.push(p)
                });
                setPosts(arrPost);
            })
    }, [postListApi])

    return (
        <div className="postList">
            {posts.map(post => <Post post={post}/>)}
        </div>
    )
}

export default PostList;
