    useEffect(() => {
        axios.get(postListApi)
            .then((response) => {
                const arrPost = [];
                const curDate = new Date();
                response.data.forEach(p => {
                    if (curDate < new Date(p.closingDate))
                        arrPost.push(p)
                });
                setPosts(arrPost);
            })
    }, [postListApi])

    return (
        <div className="postList">
            {posts.map(post => <Post post={post}/>)}
        </div>
    )
}

export default PostList;
