import './App.css';
import Header from './layout/js/Header';
import Body from './layout/js/Body'
import {useRef, useState} from "react";

function App() {
    const filter = useRef(['', '', '', '', '', '']);
    const [postListApi, setPostListApi] = useState("http://localhost:3000/postData.json");

    const getFilter = () => filter.current;
    const setFilter = (index, condition) => {filter.current[index] = condition};
    const getPostListApi = () => postListApi;

    return (
        <div className="App">
            <Header getFilter={getFilter}/>
            <Body setFilter={setFilter} getPostListApi={getPostListApi}/>
        </div>
    );
}

export default App;
