import "../css/post.css";
import Timer from "./Timer";

function Post({post}) {
    return (
        <div
            key={post.postId}
            className="post" style={{backgroundImage:`url(${post.thumbnail})`}}
            onClick={() => {window.open(post.url)}}
        >
            <div className="top">

            </div>
            <div className="bottom">
                <p>{post.title}</p>
                <Timer dateTime={post.closingDate}/>
                {post.conditions.map(c => <span>{c}</span>)}
            </div>
        </div>
    )
}

export default Post;
