import '../css/post-list.css';
import {useEffect, useState} from "react";
import axios from "axios";
import Post from "./Post";

function PostList() {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:3000/postData.json")
            .then((response) => {
                const arrPost = [];
                response.data.forEach(p => arrPost.push(p));
                setPosts(arrPost);
            })
    }, [])

    return (
        <div className="postList">
            {posts.map(post => {
                if (new Date() < new Date(post.closingDate))
                    return <Post post={post}/>
            })}
        </div>
    )
}

export default PostList;
