import {useState} from "react";
import Main from "../../component/js/Main";
import Post from "../../component/js/Post";
import "../css/body.css";

function Body({setFilter}) {
    const [page, setPage] = useState(<Main/>);
    const [pageName, setPageName] = useState('main');

    const newSetFilter = (index, condition) => {
        setFilter(index, condition);
    }

    function changePage() {
        setPage((pageName === 'main' ? <Post/> : <Main newSetFilter={newSetFilter}/>));
        setPageName((pageName === 'main' ? 'post' : 'main'));
    }

    return (
        <div className="body">
            {page}
        </div>
    )
}

export default Body;
