import './App.css';
import Header from './layout/js/Header';
import {useRef, useState} from "react";
import PostPage from "./component/js/PostPage";
import Filter from "./component/js/filter/Filter";
import PostList from "./component/js/PostList";
import {Button} from "react-bootstrap";

function App() {
    const filter = useRef(['', '', '', '', '', '']);

    const setFilter = (index, condition) => {filter.current[index] = condition};

    const [api, setApi] = useState("http://localhost:3000/postData.json");
    const [pageName, setPageName] = useState('main');
    const [page, setPage] = useState(<PostList api={api}/>);

    const setSearchApi = () => {
        let district = filter.current[0] + filter.current[1] + filter.current[2] + "&";
        let benefit = (filter.current[3] === '' ? '' : 'benefit=' + filter.current[3] + "&");
        let job = (filter.current[4] === '' ? '' : 'job=' + filter.current[4] + "&");
        let sex = (filter.current[5] === '' ? '' : 'sex=' + filter.current[5] + "&");
        let value = document.getElementById('search').value;
        let search = (value === '' ? '' : 'search=' + value);
        let conditions = district + benefit + job + sex + search;

        setApi("http://localhost:3000/postData.json" + (conditions === '' ? '' : '?' + conditions));
    };

    function changePage() {
        setPage((pageName === 'main' ? <PostPage/> : <PostList api={api}/>));
        setPageName((pageName === 'main' ? 'post' : 'main'));
    }

    return (
        <div className="App">
            {api}
            <Header setSearchApi={setSearchApi}/>
            <Filter setFilter={setFilter}/>
            {page}
            <Button onClick={changePage}>{pageName}</Button>
        </div>
    );
}

export default App;
