import '../css/post-list.css';
import {useEffect, useRef, useState} from "react";
import axios from "axios";
import Post from "./Post";
import { useInView } from 'react-intersection-observer';

function PostList({postListApi}) {
    const [posts, setPosts] = useState([]);
    const postNum = useRef(0);
    const [ref, inView, entry] = useInView({threshold:0.2});

    const getPostList = () => {
        axios.get(postListApi + "offset=" + postNum.current)
            .then((response) => {
                const arrPost = [];
                const curDate = new Date();
                response.data.forEach(p => {
                    if (curDate < new Date(p.closingDate))
                        arrPost.push(p)
                });
                setPosts(prevPosts => prevPosts.concat(arrPost));
                postNum.current += response.data.length;
                if (response.data.length === 20) {
                    entry = arrPost[arrPost.length - 1];
                } else {
                    ref.current = null;
                }
            });
    };

    useEffect(() => {
        if (ref !== null) {
            getPostList();
        }
    }, [inView]);

    return (
        <div className="postList">
            {posts.map(post => <Post key={post.postId} post={post}/>)}
        </div>
    )
}

export default PostList;
